def timer(func):
    def nor_func(*args, **kwargs):
        print("starting")
        st = time.perf_counter()

        res = func(*args, **kwargs)
        duration = time.perf_counter() - st

        print("done")
        print(duration)
        return res

    return nor_func


def rounder(func):
    def wrapper(*args, **kwargs): # ’£÷Ä’Ø’∏’≤
        res = func(*args, **kwargs)
        return round(res, 2)
    return wrapper


